    .gb_Ac {
      background-color: @crust;
      color: @text;
    }

    /* Buttons */

    /* Solid primary */
    .T-I-atl {
      background-color: @accent-color;
      color: @crust;
    }

    /* Transparent primary */
    .l0,
    div.b8 .a8k,
    .bAo > .ad {
      color: @accent-color;

      &::before {
        background-color: @accent-color;
      }
    }

    /* Transparent secondary */
    .bs0 > .acM,
    .bti > .btg,
    .a8Y > .T-I-ax7 {
