      --warm-gray-60-a25: fadeout(@base, 75);
      --warm-gray-60-a30: fadeout(@base, 70);
      --warm-gray-60-a35: fadeout(@base, 65);
    }

    .share-promoted-detour-button-legacy {
      background-color: @accent-color !important;
    }
  }
}

