                --divider-color: rgba(var(--button-color), 0.20) !important;
                --yt-spec-brand-link-text: var(--red) !important;
                --yt-spec-static-grey: rgba(var(--white, var(--main-backgrounda)), 0.2) !important;
                --yt-deprecated-white-opacity-lighten-4: rgba(var(--button-color), 0.10) !important;
            }

            html,
            html[dark] {
                background-color: rgb(var(--main-background)) !important;
            }

            html[darker-dark-theme],
            html[darker-dark-theme] [dark] {
                --yt-spec-text-primary: rgb(var(--main-text)) !important;
                --yt-spec-text-primary-inverse: rgb(var(--black, var(--white, var(--main-background)))) !important;
            }

            html[system-icons],
            html[system-icons] [dark] {
                --yt-spec-icon-disabled: rgba(var(--main-text), 0.38) !important;
                --yt-spec-brand-icon-inactive: rgb(var(--main-text)) !important;
                --yt-spec-icon-inactive: rgb(var(--main-text)) !important;
            }

            /* Text selection */
            ::selection {
                color: rgb(var(--black, var(--white, var(--main-background)))) !important;
                background: rgb(var(--main-color)) !important;
            }

            /* Tooltip */
            .tp-yt-paper-tooltip[style-target=tooltip] {
                color: rgb(var(--black, var(--white, var(--main-background))));
            }

            /* Input fields */
            ytd-masthead ::-webkit-input-placeholder {
                color: var(--secondary-text) !important;
