        ytd-guide-entry-renderer[is-header] .arrow-icon.ytd-guide-entry-renderer path {
            d: path("M12 20.5c1.894 0 3.643-.62 5.055-1.666a5.5 5.5 0 00-10.064-.105.755.755 0 01-.054.099A8.462 8.462 0 0012 20.5Zm4.079-5.189a7 7 0 012.142 2.48 8.5 8.5 0 10-12.443 0 7 7 0 0110.3-2.48ZM12 22c5.523 0 10-4.477 10-10S17.523 2 12 2 2 6.477 2 12s4.477 10 10 10Zm2-12.5a2 2 0 11-4 0 2 2 0 014 0Zm1.5 0a3.5 3.5 0 11-7 0 3.5 3.5 0 017 0Z");
            transform: scale(0.667);
            clip-rule: evenodd;
            fill-rule: evenodd;
        }

        ytd-guide-entry-renderer[is-header][active] .arrow-icon.ytd-guide-entry-renderer path {
            d: path("M18.37 19.709A9.98 9.98 0 0022 12c0-5.523-4.477-10-10-10S2 6.477 2 12a9.98 9.98 0 003.63 7.709A9.96 9.96 0 0012 22a9.96 9.96 0 006.37-2.291ZM6.15 18.167a6.499 6.499 0 0111.7 0A8.47 8.47 0 0112 20.5a8.47 8.47 0 01-5.85-2.333ZM15.5 9.5a3.5 3.5 0 11-7 0 3.5 3.5 0 017 0Z");
            color: var(--yt-spec-brand-icon-active);
        }

        ytd-guide-entry-renderer[is-header] .arrow-icon.ytd-guide-entry-renderer {
            width: 24px;
            height: 24px;
