    if theater {
        ytd-watch-flexy[theater]:not([fullscreen]) {
            #full-bleed-container.ytd-watch-flexy {
                height: 100vh;
                max-height: none;
                margin-top: calc(0px - var(--ytd-toolbar-height));
            }
        }

        ytd-watch-flexy[fixed-panels] #chat.ytd-watch-flexy {
            top: 0;
        }

        #masthead[is-watch-page][theater]:not([fullscreen]) {
            position: absolute;
            transform: translateY(calc(0px - var(--ytd-toolbar-height)));
            padding-bottom: 20px;
            transition: transform .3s ease, padding .3s ease;
            &:hover {
                transform: initial;
                padding-bottom: 0;
            }
        }

        #player.skeleton.theater {
            height: 100vh;
            margin-top: 0;
            max-height: none;
        }
    }

    if ambient-mode {
        ytd-watch-flexy:not([default-layout]) #player.ytd-watch-flexy {
            display: block !important;
            position: absolute;
            top: 30px;
            left: 0;
            right: 0;
        }

        ytd-watch-flexy:not([default-layout]) #cinematics.ytd-watch-flexy > div > div {
            transform: scale(1, 2) !important; 
            width: 100vw;
        }

        ytd-watch-flexy .html5-video-player {
            z-index: 2000;
        }

        body {
            overflow-x: hidden;
        }

        body:has(#player-container[style="touch-action: auto;"]) {
            --headerBgOpacity: 0;
        }

        ytd-masthead[is-watch-page] #background.ytd-masthead {
            transition: opacity .25s;
            opacity: var(--headerBgOpacity, 1);
