/* ==UserStyle==
@name WhatsApp Web Catppuccin
@namespace github.com/catppuccin/userstyles/styles/whatsapp-web
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/whatsapp-web
@version 2024.12.31
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/whatsapp-web/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Awhatsapp-web
@description Soothing pastel theme for WhatsApp Web
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]

@var checkbox lighterMessages "Lighter incoming messages" 0
==/UserStyle== */

@-moz-document domain("web.whatsapp.com") {
  body {
    #catppuccin(@lightFlavor);
  }

  body.dark {
    #catppuccin(@darkFlavor);
  }

  #catppuccin(@flavor) {
    @rosewater: @catppuccin[@@flavor][@rosewater];
    @flamingo: @catppuccin[@@flavor][@flamingo];
    @pink: @catppuccin[@@flavor][@pink];
    @mauve: @catppuccin[@@flavor][@mauve];
    @red: @catppuccin[@@flavor][@red];
    @maroon: @catppuccin[@@flavor][@maroon];
    @peach: @catppuccin[@@flavor][@peach];
    @yellow: @catppuccin[@@flavor][@yellow];
    @green: @catppuccin[@@flavor][@green];
    @teal: @catppuccin[@@flavor][@teal];
    @sky: @catppuccin[@@flavor][@sky];
    @sapphire: @catppuccin[@@flavor][@sapphire];
    @blue: @catppuccin[@@flavor][@blue];
    @lavender: @catppuccin[@@flavor][@lavender];
    @text: @catppuccin[@@flavor][@text];
    @subtext1: @catppuccin[@@flavor][@subtext1];
    @subtext0: @catppuccin[@@flavor][@subtext0];
    @overlay2: @catppuccin[@@flavor][@overlay2];
    @overlay1: @catppuccin[@@flavor][@overlay1];
    @overlay0: @catppuccin[@@flavor][@overlay0];
    @surface2: @catppuccin[@@flavor][@surface2];
    @surface1: @catppuccin[@@flavor][@surface1];
    @surface0: @catppuccin[@@flavor][@surface0];
    @base: @catppuccin[@@flavor][@base];
    @mantle: @catppuccin[@@flavor][@mantle];
    @crust: @catppuccin[@@flavor][@crust];
    @accent: @catppuccin[@@flavor][@@accentColor];

    color-scheme: if(@flavor = latte, light, dark);

    ::selection {
      background-color: fade(@accent, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    .landing-wrapper-before {
      background-color: @accent !important;
    }

    /* STATUS PAGE */
    --status-background: @base !important;
    .bfsx6evv {
      background-color: @mantle !important;
    }

    --modal-backdrop-solid: @base !important;

    /* join group pop up */
    --modal-background: @base !important;

    /* startup progress */
    --progress-primary: @accent !important;
    --progress-background: @surface1 !important;
    --startup-background: @base !important;
    --startup-background-rgb: #rgbify(@base)[];
    /* home page, no chat selected */
    --intro-background: @base !important;
    --intro-border: @accent !important;
    /* general background */
    --app-background: @base !important;

    /* CHAT LIST */
    /* nav bar background */
    --navbar-background: @mantle !important;
    /* filters container background */
    --filters-container-background: @base !important;
    /* filters item background */
    --filters-item-background: @surface0 !important;
    /* filters item color */
    --filters-item-color: @subtext0 !important;
    /* filters item background hover */
    --filters-item-background-hover: @surface1 !important;
    /* filters item active background */
    --filters-item-active-background: fade(@accent, 30%) !important;
    /* filters item active color */
    --filters-item-active-color: @accent !important;
    /* chat list background */
    --background-default: @base !important;
    /* chat list header */
    --panel-header-background: @mantle !important;
    /* icons */
    --panel-header-icon: @text !important;
    /* other warnings (notifications) */
    --butterbar-default-background: @mantle !important;
    --butterbar-notification-icon: @sky !important;
    /* loading messages warning */
    --butterbar-green-nux-background: @mantle !important;
    /* update available warning */
    --butterbar-update-background: @mantle !important;
    --butterbar-update-icon: @accent !important;
    /* chat list search bar */
    --search-container-background: @base !important;
    --search-input-container-background: @base !important;
    --search-input-background: @surface0 !important;
    --search-input-container-background-active: @mantle !important;
    --icon-search-back: @accent !important;
    /* archive icon */
    --icon-bright-highlight: @accent !important;
    /* archived unread marker */
    --unread-marker-background: @accent !important;
    --unread-timestamp: @accent !important;
    /* archived chats header */
    --archived-chat-persistent-header-background: @crust !important;
    /* unread voice message */
    --ptt-green: @green !important;
    /* read voice message */
    --ptt-blue: @blue !important;
    /* pinned chat icon */
    --icon-pinned: @accent !important;
    /* last message in selected chat */
    --secondary-stronger: @subtext1 !important;
    /* background for active and hover chats in chat list */
    --background-default-active: @surface1 !important;
    --background-default-hover: @surface0 !important;
    /* unread chats filter */
    --icon-high-emphasis: @green !important;
    .p7idzaix {
      color: @crust !important;
    }
    /* voice message playing */
    --ptt-ooc-background: @mantle !important;

    /* CONVERSATION */
    /* chat background */
    --conversation-panel-background: @mantle !important;
    /* MESSAGES */
    /* background for incoming and outgoing messages */
    & when (@lighterMessages = 1) {
      --incoming-background: @surface1 !important;
      --incoming-background-deeper: @surface2 !important;
      /* arrow icon in messages */
      --incoming-background-rgb: #rgbify(@surface1)[];
    }
    & when (@lighterMessages = 0) {
      --incoming-background: @base !important;
      --incoming-background-deeper: @surface0 !important;
      /* arrow icon in messages */
      --incoming-background-rgb: #rgbify(@base)[];
    }
    --outgoing-background: @surface0 !important;
    /* read double tick */
    --icon-ack: @blue !important;
    /* received double tick */
    --bubble-meta-icon: @subtext0 !important;
    /* message timestamp */
    --bubble-meta: @text !important;
    --message-primary: @text !important;
    /* POLL */
    /* creation */
    --poll-modal-background-color: @base !important;
    --poll-modal-footer-background-color: @surface0 !important;
    /* votes */
    --poll-bar-fill-receiver: @green !important;
    --poll-bar-fill-sender: @green !important;
    --checkbox-mark: @crust !important;

    /* audio duration */
    --audio-progress-metadata: @subtext0 !important;
    /* system messages (unread or day messages in chats) */
    --system-message-text: @text !important;
    --system-message-background: @crust !important;
    /* unread system message background */
    --unread-background: @crust !important;
    --unread-bar-background: @surface1 !important;
    /* meta data for documents */
    --document-meta: @subtext1 !important;
    /* mentions, links and link previews */
    --mention-at-symbol: @sapphire !important;
    --link: @sapphire !important;
    --link-preview: @text !important;
    --link-preview-lighter: @subtext1 !important;
    --link-preview-light: @subtext0 !important;
    --outgoing-background-rgb: #rgbify(@surface0)[];
    /* QUOTED MESSAGES */
    /* quoted messages */
    --quoted-message-text: @text !important;
    /* background for quoted incoming and outgoing messages */
    --outgoing-background-deeper: @surface1 !important;

    /* SEARCH IN CHAT HIGHLIGHT */
    --highlight: @teal !important;

    /* quote colors */
    .bg-color-1 {
      background-color: @green !important;
    }
    .color-1 {
      color: @green !important;
    }
    .bg-color-2 {
      background-color: @blue !important;
    }
    .color-2 {
      color: @blue !important;
    }
    .bg-color-3 {
      background-color: @pink !important;
    }
    .color-3 {
      color: @pink !important;
    }
    .bg-color-4 {
      background-color: @sapphire !important;
    }
    .color-4 {
      color: @sapphire !important;
    }
    .bg-color-5 {
      background-color: @peach !important;
    }
    .color-5 {
      color: @peach !important;
    }
    .bg-color-6 {
      background-color: @sky !important;
    }
    .color-6 {
      color: @sky !important;
    }
    .bg-color-7 {
      background-color: @yellow !important;
    }
    .color-7 {
      color: @yellow !important;
    }
    .bg-color-8 {
      background-color: @teal !important;
    }
    .color-8 {
      color: @teal !important;
    }
    .bg-color-9 {
      background-color: @lavender !important;
    }
    .color-9 {
      color: @lavender !important;
    }
    .bg-color-10 {
      background-color: @red !important;
    }
    .color-10 {
      color: @red !important;
    }
    .bg-color-11 {
      background-color: @mauve !important;
    }
    .color-11 {
      color: @mauve !important;
    }
    .bg-color-12 {
      background-color: @flamingo !important;
    }
    .color-12 {
      color: @flamingo !important;
    }
    .bg-color-13 {
      background-color: @rosewater !important;
    }
    .color-13 {
      color: @rosewater !important;
    }
    .bg-color-14 {
      background-color: @pink !important;
    }
    .color-14 {
      color: @pink !important;
    }
    .bg-color-15 {
      background-color: @maroon !important;
    }
    .color-15 {
      color: @maroon !important;
    }
    .bg-color-16 {
      background-color: @teal !important;
    }
    .color-16 {
      color: @teal !important;
    }
    /* general text */
    --primary: @text !important;
    --primary-strong: @text !important;
    --primary-stronger: @text !important;
    --primary-strongest: @text !important;
    --secondary: @subtext0 !important;
    --secondary-strongest-rgb: #rgbify(@text)[];
    --inline-code-text: @blue !important;
    /* chat list typing message */
    --typing: @green !important;
    /* background for video player and image viewer */
    --media-viewer-background: @mantle !important;
    /* not loaded media */
    --media-gallery-thumb-background: @crust !important;

    /* Group info/Contact info */
    --photopicker-overlay-background: fade(@mantle, 20%) !important;
    --photopicker-overlay-background-rgb: #rgbify(@base)[];
    --media-viewer-background-rgb: #rgbify(@base)[];
    --drawer-background-deep: @crust !important;
    /* group info read more */
    --input-button-more: @sapphire !important;
    /* Group info thumbnails border */
    --chat-info-drawer-thumb-background: @surface2 !important;
    /* Group admin badge background */
    --chat-marker-background: @mantle !important;
    --chat-marker-border: @mantle !important;
    /* Group admin badge foreground */
    --chat-marker: @subtext0 !important;
    /* Media gallery */
    --drawer-gallery-background: @crust !important;
    /* mute toggle */
    --switch-button-checked-color: @accent !important;
    --switch-track-checked-color: @surface2 !important;
    --switch-track-color: @surface2 !important;
    --switch-button-color: @overlay1 !important;

    /* danger, block, exit and report buttons */
    --danger: @red !important;

    /* group added by someone not in contacts */
    --button-plain-background: @surface0 !important;
    --button-plain-background-hover: @surface1 !important;

    /* EMOJI AND STICKERS */
    --panel-input-background: @surface0 !important;
    --sticker-button-background: @surface1 !important;
    --active-tab-marker: @accent !important;

    /* REACTIONS */
    --reactions-panel-background-color: @surface0 !important;
    --reactions-tray-background: @surface0 !important;
    --reactions-details-background: @surface0 !important;
    --svg-gray-button: @surface0 !important;

    /* Forward message popup */
    --panel-background-colored-deeper: @crust !important;
    --modal-backdrop: fade(@mantle, 20%) !important;

    /* Forward media caption */
    --forward-caption-preview-background: @mantle !important;
    --forward-caption-preview-content: @crust !important;

    /* MEDIA EDITOR */
    /* background for media editor */
    --panel-background-deeper: @mantle !important;
    /* message box for media editor */
    --media-editor-image-caption-input-background: @surface0 !important;
    /* selected picture in media editor */
    --media-editor-thumb-border-active: @accent !important;
    /* send button in media editor */
    --button-round-background: @accent !important;

    /* COMPOSE BAR */
    /* type a message bar and background */
    --compose-input-background: @surface0 !important;
    --compose-input-border: @surface1 !important;
    --compose-panel-background: @mantle !important;
    --rich-text-panel-background: @mantle !important;
    /* select messages fixes */
    --panel-background: @mantle !important;
    --panel-background-rgb: #rgbify(@mantle)[];
    --checkbox-background: @accent !important;
    /* quoted message in compose */
    --popup-panel-background: @surface0 !important;
    /* use Surface 0 for contrast, other colors don't pass AAA */
    /* mention list item background */
    --compose-panel-background-hover: @surface1 !important;
    /* compose bar icons */
    --icon: @accent !important;
    /* ATTACH ICONS */
    /* poll icon */
    div._1OT67
      > div
      > span
      > div
      > div
      > ul
      > li:nth-child(6)
      > button
      > span
      > svg
      > circle {
      fill: @green !important;
    }
    /* image icon */
    div._1OT67
      > div
      > span
      > div
      > div
      > ul
      > li:nth-child(1)
      > button
      > span
      > svg
      > g:nth-child(1)
      > g
      > path:nth-child(2) {
      fill: @pink !important;
    }
    div._1OT67
      > div
      > span
      > div
      > div
      > ul
      > li:nth-child(1)
      > button
      > span
      > svg
      > g:nth-child(1)
      > g
      > path:nth-child(1) {
      fill: @pink !important;
      filter: brightness(85%) !important;
    }
    div._1OT67
      > div
      > span
      > div
      > div
      > ul
      > li:nth-child(1)
      > button
      > span
      > svg
      > g:nth-child(1)
      > g
      > rect {
      fill: @pink !important;
      filter: brightness(70%) !important;
    }
    /* sticker icon */
    div._1OT67
      > div
      > span
      > div
      > div
      > ul
      > li:nth-child(2)
      > button
      > span
      > svg
      > g
      > circle {
      fill: @blue !important;
      filter: brightness(85%) !important;
    }
    div._1OT67
      > div
      > span
      > div
      > div
      > ul
      > li:nth-child(2)
      > button
      > span
      > svg
      > g
      > path:nth-child(2) {
      fill: @blue !important;
    }
    /* camera icon */
    div._1OT67
      > div
      > span
      > div
      > div
      > ul
      > li:nth-child(3)
      > button
      > span
      > svg
      > g:nth-child(1)
      > g
      > path:nth-child(1) {
      fill: @red !important;
      filter: brightness(85%) !important;
    }
    div._1OT67
      > div
      > span
      > div
      > div
      > ul
      > li:nth-child(3)
      > button
      > span
      > svg
      > g:nth-child(1)
      > g
      > path:nth-child(2) {
      fill: @red !important;
    }
    div._1OT67
      > div
      > span
      > div
      > div
      > ul
      > li:nth-child(3)
      > button
      > span
      > svg
      > g:nth-child(1)
      > g
      > rect {
      fill: @red !important;
      filter: brightness(85%) !important;
    }
    /* document icon */
    div._1OT67
      > div
      > span
      > div
      > div
      > ul
      > li:nth-child(4)
      > button
      > span
      > svg
      > g:nth-child(1)
      > g
      > path:nth-child(1) {
      fill: @mauve !important;
      filter: brightness(85%) !important;
    }
    div._1OT67
      > div
      > span
      > div
      > div
      > ul
      > li:nth-child(4)
      > button
      > span
      > svg
      > g:nth-child(1)
      > g
      > path:nth-child(2) {
      fill: @mauve !important;
    }
    /* contact icon */
    div._1OT67
      > div
      > span
      > div
      > div
      > ul
      > li:nth-child(5)
      > button
      > span
      > svg
      > g:nth-child(1)
      > g
      > g
      > path:nth-child(1) {
      fill: @sky !important;
      filter: brightness(85%) !important;
    }
    div._1OT67
      > div
      > span
      > div
      > div
      > ul
      > li:nth-child(5)
      > button
      > span
      > svg
      > g:nth-child(1)
      > g
      > g
      > path:nth-child(2) {
      fill: @sky !important;
    }
    /* VOICE MESSAGE SENDING */
    --ptt-draft-button-send: @accent !important;
    --ptt-draft-waveform-background: @base !important;
    --ptt-draft-button-stop: @red !important;
    --ptt-draft-button-stop-hover: @maroon !important;

    /* GENERAL OPTIONS */
    /* dropdown menus */
    --dropdown-background: @surface0 !important;
    --dropdown-background-hover: @surface1 !important;

    /* OTHER SECTIONS */
    /* profile background */
    --drawer-background: @base !important;
    /* communities, new chat, other panes */
    --panel-background-colored: @mantle !important;
    --drawer-section-background: @base !important;
    /* new chat icons */
    --round-icon-background: @green !important;
    --inverse: @text !important;
    /* title and icon for drawers */
    --drawer-header-title: @text !important;
    /* buttons such as create community */
    --button-primary-background: @accent !important;
    --button-primary-background-hover: fade(@accent, 20%) !important;
    /* keyboard shortcuts background */
    --panel-background-lighter: @mantle !important;
    /* active input in modals and drawers */
    --input-border-active: @accent !important;

    /* Start page drawing */
    #app
      > div
      > div
      > div._2Ts6i._2xAQV
      > div
      > div
      > div.WM0_u
      > span
      > svg
      > path:nth-child(1) {
      fill: @surface2 !important;
    }

    /* Status unread dot */
    #df9d3429-f0ef-48b5-b5eb-f9d27b2deba6 > path:nth-child(2) {
      fill: @accent !important;
    }

    #side
      > div._3gYev
      > div
      > div
      > button
      > div._3xdht._1ZD3q
      > span
      > svg
      > path {
      fill: @accent !important;
    }
  }
}

#rgbify(@color) {
  @rgb: red(@color) green(@color) blue(@color);
}

/* deno-fmt-ignore */
@catppuccin: {
  @latte:     { @rosewater: #dc8a78; @flamingo: #dd7878; @pink: #ea76cb; @mauve: #8839ef; @red: #d20f39; @maroon: #e64553; @peach: #fe640b; @yellow: #df8e1d; @green: #40a02b; @teal: #179299; @sky: #04a5e5; @sapphire: #209fb5; @blue: #1e66f5; @lavender: #7287fd; @text: #4c4f69; @subtext1: #5c5f77; @subtext0: #6c6f85; @overlay2: #7c7f93; @overlay1: #8c8fa1; @overlay0: #9ca0b0; @surface2: #acb0be; @surface1: #bcc0cc; @surface0: #ccd0da; @base: #eff1f5; @mantle: #e6e9ef; @crust: #dce0e8; };
  @frappe:    { @rosewater: #f2d5cf; @flamingo: #eebebe; @pink: #f4b8e4; @mauve: #ca9ee6; @red: #e78284; @maroon: #ea999c; @peach: #ef9f76; @yellow: #e5c890; @green: #a6d189; @teal: #81c8be; @sky: #99d1db; @sapphire: #85c1dc; @blue: #8caaee; @lavender: #babbf1; @text: #c6d0f5; @subtext1: #b5bfe2; @subtext0: #a5adce; @overlay2: #949cbb; @overlay1: #838ba7; @overlay0: #737994; @surface2: #626880; @surface1: #51576d; @surface0: #414559; @base: #303446; @mantle: #292c3c; @crust: #232634; };
  @macchiato: { @rosewater: #f4dbd6; @flamingo: #f0c6c6; @pink: #f5bde6; @mauve: #c6a0f6; @red: #ed8796; @maroon: #ee99a0; @peach: #f5a97f; @yellow: #eed49f; @green: #a6da95; @teal: #8bd5ca; @sky: #91d7e3; @sapphire: #7dc4e4; @blue: #8aadf4; @lavender: #b7bdf8; @text: #cad3f5; @subtext1: #b8c0e0; @subtext0: #a5adcb; @overlay2: #939ab7; @overlay1: #8087a2; @overlay0: #6e738d; @surface2: #5b6078; @surface1: #494d64; @surface0: #363a4f; @base: #24273a; @mantle: #1e2030; @crust: #181926; };
  @mocha:     { @rosewater: #f5e0dc; @flamingo: #f2cdcd; @pink: #f5c2e7; @mauve: #cba6f7; @red: #f38ba8; @maroon: #eba0ac; @peach: #fab387; @yellow: #f9e2af; @green: #a6e3a1; @teal: #94e2d5; @sky: #89dceb; @sapphire: #74c7ec; @blue: #89b4fa; @lavender: #b4befe; @text: #cdd6f4; @subtext1: #bac2de; @subtext0: #a6adc8; @overlay2: #9399b2; @overlay1: #7f849c; @overlay0: #6c7086; @surface2: #585b70; @surface1: #45475a; @surface0: #313244; @base: #1e1e2e; @mantle: #181825; @crust: #11111b; };
};

/* ==UserStyle==
@name Arch Wiki Catppuccin
@namespace github.com/catppuccin/userstyles/styles/arch-wiki
@homepageURL https://github.com/catppuccin/userstyles/tree/main/styles/arch-wiki
@version 2024.12.31
@updateURL https://github.com/catppuccin/userstyles/raw/main/styles/arch-wiki/catppuccin.user.less
@supportURL https://github.com/catppuccin/userstyles/issues?q=is%3Aopen+is%3Aissue+label%3Aarch-wiki
@description Soothing pastel theme for Arch Wiki
@author Catppuccin
@license MIT

@preprocessor less
@var select lightFlavor "Light Flavor" ["latte:Latte*", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha"]
@var select darkFlavor "Dark Flavor" ["latte:Latte", "frappe:Frappé", "macchiato:Macchiato", "mocha:Mocha*"]
@var select accentColor "Accent" ["rosewater:Rosewater", "flamingo:Flamingo", "pink:Pink", "mauve:Mauve*", "red:Red", "maroon:Maroon", "peach:Peach", "yellow:Yellow", "green:Green", "teal:Teal", "blue:Blue", "sapphire:Sapphire", "sky:Sky", "lavender:Lavender", "subtext0:Gray"]
==/UserStyle== */

@-moz-document domain("wiki.archlinux.org") {
  :root {
    @media (prefers-color-scheme: light) {
      #catppuccin(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
      #catppuccin(@darkFlavor);
    }
  }

  #catppuccin(@flavor) {
    @rosewater: @catppuccin[@@flavor][@rosewater];
    @flamingo: @catppuccin[@@flavor][@flamingo];
    @pink: @catppuccin[@@flavor][@pink];
    @mauve: @catppuccin[@@flavor][@mauve];
    @red: @catppuccin[@@flavor][@red];
    @maroon: @catppuccin[@@flavor][@maroon];
    @peach: @catppuccin[@@flavor][@peach];
    @yellow: @catppuccin[@@flavor][@yellow];
    @green: @catppuccin[@@flavor][@green];
    @teal: @catppuccin[@@flavor][@teal];
    @sky: @catppuccin[@@flavor][@sky];
    @sapphire: @catppuccin[@@flavor][@sapphire];
    @blue: @catppuccin[@@flavor][@blue];
    @lavender: @catppuccin[@@flavor][@lavender];
    @text: @catppuccin[@@flavor][@text];
    @subtext1: @catppuccin[@@flavor][@subtext1];
    @subtext0: @catppuccin[@@flavor][@subtext0];
    @overlay2: @catppuccin[@@flavor][@overlay2];
    @overlay1: @catppuccin[@@flavor][@overlay1];
    @overlay0: @catppuccin[@@flavor][@overlay0];
    @surface2: @catppuccin[@@flavor][@surface2];
    @surface1: @catppuccin[@@flavor][@surface1];
    @surface0: @catppuccin[@@flavor][@surface0];
    @base: @catppuccin[@@flavor][@base];
    @mantle: @catppuccin[@@flavor][@mantle];
    @crust: @catppuccin[@@flavor][@crust];
    @accent: @catppuccin[@@flavor][@@accentColor];

    color-scheme: if(@flavor = latte, light, dark);

    ::selection {
      background-color: fade(@accent, 30%);
    }

    input,
    textarea {
      &::placeholder {
        color: @subtext0 !important;
      }
    }

    #content table,
    #content h1,
    #content h2,
    #content h3,
    #content h4,
    #content h5,
    #content pre,
    #content code,
    #content tt {
      color: @text !important;
    }

    body.skin-vector-2022 .vector-sticky-header {
      color: @text !important;
      background-color: @mantle !important;
      border-bottom-color: @blue !important;
    }

    #vector-main-menu-pinned-container,
    #vector-page-tools-pinned-container {
      background-color: @base !important;
    }

    #ooui-php-191,
    #preferences .mw-htmlform-submit-buttons {
      background-color: @mantle !important;
    }

    .mw-echo-ui-notificationItemWidget-content-message-header,
    .oo-ui-labelElement-label {
      color: @text !important;
    }

    .oo-ui-optionWidget-selected {
      background-color: @surface2 !important;
    }

    .oo-ui-buttonElement-framed.oo-ui-widget-disabled
      > .oo-ui-buttonElement-button {
      background-color: @surface0 !important;
      color: @text !important;
      border-color: @surface2 !important;
      filter: brightness(0.4);
      cursor: not-allowed;
    }

    .mw-htmlform-ooui .mw-htmlform-matrix tbody tr:nth-child(even) td {
      background-color: @surface1 !important;
    }

    .mw-htmlform-ooui .mw-htmlform-matrix tbody tr:hover td {
      background-color: @surface2 !important;
    }

    .mw-htmlform-ooui .mw-htmlform-matrix tbody tr:nth-child(odd) td,
    .mw-rcfilters-ui-filterTagMultiselectWidget.oo-ui-widget-enabled
      .oo-ui-tagMultiselectWidget-handle,
    .oo-ui-tagMultiselectWidget.oo-ui-widget-enabled.oo-ui-tagMultiselectWidget-outlined
      .oo-ui-tagItemWidget.oo-ui-widget-enabled,
    .mw-rcfilters-ui-cell.mw-rcfilters-ui-filterTagMultiselectWidget-views-select,
    .mw-rcfilters-ui-changesListWrapperWidget .mw-changeslist-legend,
    .oo-ui-popupWidget-popup,
    .mw-echo-ui-notificationItemWidget,
    .mw-echo-ui-placeholderItemWidget {
      background-color: @surface0 !important;
    }

    .mw-echo-ui-notificationsInboxWidget-toolbarWrapper {
      background-color: @base !important;
    }

    #archnavbar {
      background-color: @mantle !important;
      border-bottom-color: @blue !important;
    }
    .mw-parser-output {
      background-color: @base !important;
    }

    .mw-page-container {
      background-color: @base !important;
    }

    .vector-header-container {
      background-color: @base !important;
    }

    #content,
    header.mw-header li:not(.new),
    #mw-panel-toc,
    #mw-navigation li:not(.new),
    #mw-panel li:not(.new),
    #column-one li:not(.new),
    #footer {
      a:not([role="button"], .new) {
        color: @blue !important;
      }
    }

    @media screen {
      html.client-darkmode {
        background-color: @base;
      }
    }

    .vector-feature-zebra-design-disabled,
    body {
      background-color: @base;
    }

    #archnavbarlogo {
      @svg: escape(
        '<svg xmlns="http://www.w3.org/2000/svg" version="1" width="600" height="126"><path d="M159.568 34.427c-8.89-.014-16.267 1.809-19.12 2.803l-2.937 15.857c-.007.058 14.617-3.9 21.059-3.667 10.665.383 11.646 4.076 11.46 9.06.182.292-2.752-4.503-11.979-4.664-11.64-.2-28.069 4.122-28.046 21.692-.314 19.765 14.764 25.579 25.032 25.686 9.232-.168 13.563-3.496 15.934-5.28 3.115-3.257 6.679-6.532 10.078-10.462-3.216 5.844-6.005 9.884-8.907 12.977v2.611l14.033-2.361.096-38.144c-.143-5.399 3.096-26.057-26.703-26.108m-2.016 33.21c5.817.08 12.488 2.948 12.497 9.849.03 6.277-7.863 9.651-12.996 9.598-5.135-.053-11.949-4.036-11.979-10.155.099-5.47 6.426-9.432 12.478-9.291zm37.972-29.685-.095 63.166 16.348-3.15.027-35.814c.004-5.333 7.62-11.564 17.178-11.464 2.028-3.67 5.84-13.05 6.77-15.183-21.351-.051-21.623 6.137-25.336 9.18-.04-5.806-.013-9.292-.013-9.292zm92.002 8.292c-.158-.074-8.526-9.788-25.35-9.864-15.758-.262-33.433 5.847-33.716 32.27.138 23.232 16.979 32.311 33.805 32.488 18.007.187 25.172-11.26 25.602-11.543-2.149-1.863-10.196-9.837-10.196-9.837s-5.027 7.157-14.779 7.248c-9.755.093-18.234-7.54-18.354-18.189-.125-10.65 7.795-16.419 18.427-16.885 9.205-.002 14.516 5.943 14.516 5.943zm20.606-30.399-15.434 3.628.115 82.277 15.204-2.745.172-38.72c.033-4.06 5.874-10.295 15.626-10.097 9.325.097 11.41 6.215 11.384 6.988l.269 44.824 14.993-2.65.057-47.53c.099-4.574-10.018-14.233-26.28-14.302-7.729.012-12.009 1.762-14.187 3.052-3.726 2.879-7.985 5.637-12.17 9.157 3.869-4.97 7.117-8.407 10.29-10.961z" fill="@{text}" fill-rule="evenodd"/><path d="m360.136 17.218 6.962-1.742.33 82.95-7.074 1.204zm18.928 24.757 6.101-2.716.052 59.478-5.892 1.217zm-1.45-21.448 4.92-4.015 4.086 4.547-4.921 4.121zm19.024 20.365 6.962-1.421.033 12.434c.001.534 3.823-13.89 22.258-13.57 17.9.1 20.827 13.957 20.73 17.064l.221 43.725-6.102 1.324-.035-43.189c.07-1.261-2.79-11.927-15.439-11.966-12.646-.037-21.409 9.186-21.393 15.078l.1 38.047-7.07 1.847zm110.954 58.546-6.962 1.42-.033-12.433c-.001-.534-