                --yt-spec-wordmark-text: rgb(var(--main-text)) !important;
                --yt-spec-brand-icon-active: rgb(var(--main-text)) !important;
                --yt-spec-icon-active-other: rgb(var(--main-text)) !important;
                --yt-spec-call-to-action: rgb(var(--main-color)) !important;
                --yt-spec-call-to-action-inverse: rgb(var(--black, var(--white, var(--main-background)))) !important;
                --yt-spec-call-to-action-faded: rgba(var(--main-color), 0.30) !important;
                --yt-spec-themed-blue: rgb(var(--main-color)) !important;
                --yt-spec-themed-green: var(--green) !important;
                --yt-spec-menu-background: var(--button-solid-color) !important;
                --yt-spec-brand-background-primary: rgb(var(--main-background)) !important;
                --yt-spec-general-background-a: rgb(var(--main-background)) !important;
                --yt-spec-general-background-b: rgb(var(--main-background)) !important;
                --yt-spec-general-background-c: rgb(var(--main-background)) !important;
                --yt-spec-static-brand-black: rgb(var(--main-background)) !important;
                --yt-spec-static-overlay-call-to-action: currentcolor !important;
                --yt-spec-dark-blue: rgb(var(--main-color)) !important;
                --yt-spec-light-blue: rgb(var(--main-color)) !important;
                --yt-spec-grey-5: rgba(var(--main-text), 0.38) !important;
                --yt-spec-brand-background-solid: rgb(var(--main-background)) !important;
                --yt-emoji-picker-search-background-color: rgba(var(--button-color), 0.10) !important;
                --yt-emoji-picker-search-color: rgb(var(--main-text)) !important;
                --yt-emoji-picker-search-placeholder-color: var(--secondary-text) !important;
                --yt-live-chat-primary-text-color: rgb(var(--main-text)) !important;
                --yt-live-chat-secondary-text-color: var(--secondary-text) !important;
                --yt-live-chat-secondary-background-color: rgba(var(--button-color), 0.10) !important;
                --yt-live-chat-text-input-field-inactive-underline-color: rgba(var(--button-color), 0.20) !important;
                --yt-live-chat-tertiary-text-color: var(--secondary-text) !important;
                --yt-live-chat-vem-background-color: var(--button-solid-color) !important;
                --yt-live-chat-background-color: rgb(var(--main-background)) !important;
                --yt-live-chat-banner-gradient-scrim: linear-gradient( var(--main-background), transparent) !important;
                --yt-live-chat-action-panel-gradient-scrim: linear-gradient( to top, var(--main-background), transparent) !important;
                --yt-live-chat-toast-background-color: var(--button-solid-color) !important;
                --yt-live-chat-toast-action-color: rgb(var(--main-color)) !important;
                --yt-live-chat-toast-text-color: rgb(var(--main-text)) !important;
                --yt-live-chat-product-picker-hover-color: rgba(var(--button-color), 0.20) !important;
                --yt-live-chat-sub-panel-background-color-transparent: rgb(var(--main-background)) !important;
                --yt-live-chat-action-panel-background-color: rgb(var(--main-background)) !important;
                --yt-live-chat-action-panel-background-color-transparent: rgb(var(--main-background)) !important;
                --yt-live-chat-dialog-background-color: rgb(var(--main-background)) !important;
                --yt-live-chat-author-chip-verified-background-color: rgb(var(--main-color)) !important;
                --yt-live-chat-author-chip-verified-text-color: rgb(var(--black, var(--white, var(--main-background)))) !important;
                --yt-live-chat-dialog-text-color: rgb(var(--main-text)) !important;
                --yt-live-chat-header-background-color: rgb(var(--main-background)) !important;
                --yt-live-chat-slider-active-color: rgb(var(--main-color)) !important;
                --yt-live-chat-slider-container-color: rgba(var(--button-color), 0.20) !important;
                --yt-live-chat-sponsor-color: var(--green) !important;
                --yt-live-chat-moderator-color: rgb(var(--main-color)) !important;
                --yt-live-chat-slider-markers-color: rgb(var(--main-text)) !important;
                --yt-live-chat-shimmer-linear-gradient: linear-gradient( 0deg, rgba(0, 0, 0, .1) 40%, var(--button-color), rgba(0, 0, 0, 0.1) 60%) !important;
                --yt-live-chat-deleted-message-color: var(--secondary-text) !important;
                --yt-live-chat-shimmer-background-color: rgba(0, 0, 0, 0.3) !important;
                --yt-live-chat-overlay-color: rgba(0, 0, 0, 0.3) !important;
                --yt-live-chat-button-dark-text-color: rgb(var(--main-text)) !important;
                --yt-live-chat-mode-change-background-color: rgba(var(--button-color), 0.10) !important;
                --yt-live-chat-text-input-field-placeholder-color: var(--secondary-text) !important;
                --yt-compact-link-icon-color: rgb(var(--main-text)) !important;
                --yt-deprecated-blue: rgb(var(--main-color)) !important;
                --paper-radio-button-checked-color: rgb(var(--main-color)) !important;
                --paper-radio-button-checked-ink-color: rgb(var(--main-color)) !important;
                --paper-radio-button-unchecked-color: rgb(var(--main-text)) !important;
                --paper-radio-button-unchecked-ink-color: rgb(var(--main-text)) !important;
                --paper-toggle-button-checked-bar-color: rgb(var(--main-color)) !important;
                --paper-toggle-button-unchecked-bar-color: rgb(var(--main-text)) !important;
                --paper-toggle-button-checked-button-color: rgb(var(--main-color)) !important;
                --paper-toggle-button-checked-ink-color: rgb(var(--main-color)) !important;
                --paper-toggle-button-unchecked-button-color: rgb(var(--main-text)) !important;
                --paper-toggle-button-unchecked-bar-opacity: 0.38 !important;
                --paper-toggle-button-checked-bar-opacity: 0.38 !important;
                --paper-checkbox-checked-color: rgb(var(--main-color)) !important;
                --paper-checkbox-checked-ink-color: rgb(var(--main-color)) !important;
                --paper-checkbox-unchecked-color: rgb(var(--main-text)) !important;
                --paper-checkbox-unchecked-ink-color: rgb(var(--main-text)) !important;
                --paper-tooltip-background: rgb(var(--main-color)) !important;
                --yt-spec-commerce-badge-background: rgba(var(--button-color), 0.10) !important;
                --yt-spec-commerce-tonal-hover: rgba(var(--button-color), 0.20) !important;
                --yt-spec-brand-button-background: var(--red) !important;
                --divider-color: rgba(var(--button-color), 0.20) !important;
                --yt-spec-brand-link-text: var(--red) !important;
                --yt-spec-static-grey: rgba(var(--white, var(--main-backgrounda)), 0.2) !important;
                --yt-deprecated-white-opacity-lighten-4: rgba(var(--button-color), 0.10) !important;
            }

            html,
            html[dark] {
                background-color: rgb(var(--main-background)) !important;
            }

            html[darker-dark-theme],
            html[darker-dark-theme] [dark] {
                --yt-spec-text-primary: rgb(var(--main-text)) !important;
                --yt-spec-text-primary-inverse: rgb(var(--black, var(--white, var(--main-background)))) !important;
            }

            html[system-icons],
            html[system-icons] [dark] {
                --yt-spec-icon-disabled: rgba(var(--main-text), 0.38) !important;
                --yt-spec-brand-icon-inactive: rgb(var(--main-text)) !important;
                --yt-spec-icon-inactive: rgb(var(--main-text)) !important;
            }

            /* Text selection */
            ::selection {
                color: rgb(var(--black, var(--white, var(--main-background)))) !important;
                background: rgb(var(--main-color)) !important;
            }

            /* Tooltip */
            .tp-yt-paper-tooltip[style-target=tooltip] {
                color: rgb(var(--black, var(--white, var(--main-background))));
            }

            /* Input fields */
            ytd-masthead ::-webkit-input-placeholder {
                color: var(--secondary-text) !important;
            }

            ytd-searchbox[has-focus] #container.ytd-searchbox {
                border: none;
                box-shadow: none;
            }

            .focused-line.tp-yt-paper-input-container {
                border-color: rgb(var(--main-color));
            }

            .ytStandardsTextareaShapeTextAreaOutline {
                border-color: rgba(var(--button-color), 0.20);
            }

            .ytStandardsTextareaShapeLabel {
                color: var(--secondary-text);
            }

            .ytStandardsTextareaShapeTextArea {
                color: rgb(var(--main-text));
            }

            /* Dropdown menus */
            ytd-dropdown-renderer {
                --paper-dropdown-menu-focus-color: rgb(var(--main-color));
            }

            .yt-contextual-sheet-layout-wiz {
                background: var(--button-solid-color);
            }

            .yt-list-item-view-model-wiz__container--tappable:hover {
                background: rgba(var(--button-color), 0.20);
            }

            .yt-list-item-view-model-wiz__title {
                color: rgb(var(--main-text));
            }

            /* Search box */
            .ytSearchboxComponentInputBox,
            .ytSearchboxComponentSearchButton {
                background: rgba(var(--button-color), 0.10) !important;
                border-color: transparent !important;
                color: rgb(var(--main-text)) !important;
                box-shadow: none !important;
            }

            .ytSearchboxComponentClearButtonIcon {
                color: rgb(var(--main-text)) !important;
            }

            .ytSearchboxComponentClearButton:hover {
                background: rgba(var(--button-color), 0.20) !important;
            }

            /* Search suggestions box */
            .ytSearchboxComponentSuggestionsContainer {
                background: var(--button-solid-color) !important;
                border: 0 !important;
            }

            .ytSuggestionComponentSuggestion,
            .ytSuggestionComponentPersonalizedSuggestion {
                color: rgb(var(--main-text)) !important;
            }

            .ytSuggestionComponentRemoveLink {
                color: rgb(var(--main-color)) !important;
            }

            .ytSuggestionComponentHighlighted,
            .ytSuggestionComponentSuggestion:hover {
                background: rgba(var(--button-color), 0.20) !important;
            }

            .ytSearchboxComponentReportButton,
            .ytSuggestionComponentRemovedText {
                color: var(--secondary-text) !important;
            }

            /* Player */
            .ytp-play-progress,
            .ytp-scrubber-button,
            #progress.ytd-thumbnail-overlay-resume-playback-renderer,
            .ytp-chrome-controls .ytp-button[aria-pressed]:after,
            .ytp-slider-handle,
            .ytp-slider-handle:before,
            .ytp-menuitem[aria-checked=true] .ytp-menuitem-toggle-checkbox:after,
            .ytProgressBarLineProgressBarPlayed,
            .ytProgressBarPlayheadProgressBarPlayheadDot,
            .PlayerControlsProgressBarHostProgressBarPlayed,
            .PlayerControlsProgressBarHostProgressBarPlayheadDot {
                background: rgb(var(--main-color)) !important;
            }

            .ytp-panel-footer-content-link {
                color: rgb(var(--main-color)) !important;
            }

            .ytp-popup {
                background: var(--button-solid-color);
            }

            .ytp-panel-menu,
            .ytp-panel-header,
            .ytp-panel-footer,
            .ytp-popup {
                color: rgb(var(--main-text));
            }

            .ytp-menuitem-label-count,
            .ytp-menuitem-sublabel,
            .ytp-menu-label-secondary,
            .ytp-swatch-color-white,
            .ytp-premium-label {
                color: var(--secondary-text);
            }

            .ytp-menuitem[role=menuitemradio][aria-checked=true] .ytp-menuitem-label,
            .ytp-contextmenu .ytp-menuitem[aria-checked=true] .ytp-menuitem-toggle-checkbox {
                background-image: var(--check-mark);
            }

            .ytp-menuitem {
                & path:not([d="M0 0h48v48H0z"]) {
                    fill: rgb(var(--main-text));
                }
                &[aria-disabled=true] path {
                    fill: rgba(var(--main-text), 0.38);
                }
                &[aria-disabled=true] {
                    color: rgba(var(--main-text), 0.38);
                }
                &:not([aria-disabled=true]):hover {
                    background: rgba(var(--button-color), 0.20);
                }
                &[aria-haspopup=true] .ytp-menuitem-content {
                    background-image: var(--arrow);
                }
            }

            .ytp-panel-back-button {
                background-image: url('data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSIxMDAlIiBoZWlnaHQ9IjEwMCUiIHZpZXdCb3g9IjAgMCAzMiAzMiIgdmVyc2lvbj0iMS4xIj48cGF0aCBkPSJNIDE5LjQxLDIwLjA5IDE0LjgzLDE1LjUgMTkuNDEsMTAuOTEgMTgsOS41IGwgLTYsNiA2LDYgeiIgZmlsbD0iYmxhY2siIC8+PC9zdmc+');
                filter: var(--filter);
            }

            .ytp-panel-header,
            .ytp-panel-footer {
                border-color: rgba(var(--button-color), 0.20);
            }

            .ytp-tooltip-text:not(.ytp-preview:not(.ytp-text-detail) .ytp-tooltip-text) {
                background: rgb(var(--main-color));
                color: rgb(var(--black, var(--white, var(--main-background))));
            }

            .iv-branding .branding-context-container-inner {
                background: rgb(var(--main-background));
                color: rgb(var(--main-text));
            }

            .ytp-tooltip.ytp-preview:not(.ytp-tooltip.ytp-preview:not(.ytp-text-detail)) {
                background: rgb(var(--main-color));
            }

            .ytp-tooltip.ytp-text-detail .ytp-tooltip-title {
                color: rgb(var(--black, var(--white, var(--main-background))));
                opacity: 0.7;
            }

            .ytp-settings-menu .ytp-menuitem[aria-checked=true] .ytp-menuitem-toggle-checkbox {
                background: rgba(var(--main-color), 0.38);
            }

            .ytp-menuitem-toggle-checkbox {
                background: rgba(var(--main-text), 0.38);
                &:after {
                    background: rgb(var(--main-text));
                }
            }

            .html5-video-player:not(.ytp-touch-mode) {
                ::-webkit-scrollbar-track,
                ::-webkit-scrollbar {
                    background: none;
                }

                ::-webkit-scrollbar-thumb {
                    background: var(--secondary-text);
                    border: 1px solid var(--button-solid-color);
                    &:hover {
                        background: rgba(var(--main-text), 0.38);
                    }
                }
            }

            /* Ambient mode */
            #cinematics.ytd-watch-flexy {
                display: none;
            }

            /* Buttons */
            .yt-spec-touch-feedback-shape__fill {
                background: currentcolor !important;
            }

            .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--tonal {
                background: rgba(var(--button-color), 0.10);
                color: rgb(var(--main-text));
                &:hover {
                    background: rgba(var(--button-color), 0.20);
                }
            }

            .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--outline {
                color: rgb(var(--main-text));
                border-color: rgba(var(--button-color), 0.20);
                &:hover {
                    background: rgba(var(--button-color), 0.20);
                    border-color: transparent;
                }
            }

            .yt-spec-button-shape-next--call-to-action.yt-spec-button-shape-next--outline {
                color: rgb(var(--main-color));
                border-color: rgba(var(--button-color), 0.20);
                &:hover {
                    background: rgba(var(--main-color), 0.30);
                    border-color: transparent;
                }
            }

            .yt-spec-button-shape-next--size-m.yt-spec-button-shape-next--segmented-start::after {
                background: rgba(var(--button-color), 0.20);
            }

            .yt-spec-button-shape-next--disabled {
                &.yt-spec-button-shape-next--text {
                    color: rgba(var(--main-text), 0.38);
                }
                &.yt-spec-button-shape-next--tonal {
                    color: rgba(var(--main-text), 0.38);
                    background: rgba(var(--button-color), 0.10);
                }
                &.yt-spec-button-shape-next--filled {
                    background: rgba(var(--button-color), 0.10);
                    color: rgba(var(--main-text), 0.38);
                }
                &.yt-spec-button-shape-next--outline {
                    color: rgba(var(--main-text), 0.38);
                    border-color: rgba(var(--button-color), 0.20);
                }
            }

            .yt-spec-button-shape-next--call-to-action {
                &.yt-spec-button-shape-next--text {
                    color: rgb(var(--main-color));
                    &:hover {
                        background: rgba(var(--main-color), 0.30);
                    }
                }
                &.yt-spec-button-shape-next--tonal {
                    color: rgb(var(--main-color));
                    background-color: rgba(var(--main-color), 0.30);
                }
            }

            .yt-spec-button-shape-next--filled:is(.yt-spec-button-shape-next--mono, .yt-spec-button-shape-next--overlay, .yt-spec-button-shape-next--call-to-action) {
                background: rgb(var(--main-color));
                color: rgb(var(--black, var(--white, var(--main-background))));
                &:hover {
                    background: var(--filled-button-hover-color);
                }
            }

            .yt-spec-touch-feedback-shape--touch-response .yt-spec-touch-feedback-shape__stroke {
                border-color: currentcolor;
            }

            .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--text {
                color: rgb(var(--main-text));
                &:hover {
                    background: rgba(var(--button-color), 0.20);
                }
            }

            #search-clear-button .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--text {
                background: none;
                --yt-spec-touch-response: none;
            }

            yt-chip-cloud-chip-renderer[chip-style=STYLE_DEFAULT][selected],
            yt-chip-cloud-chip-renderer[chip-style=STYLE_HOME_FILTER][selected],
            .ytChipShapeActive {
                background: rgb(var(--main-color));
                color: rgb(var(--black, var(--white, var(--main-background))));
            }

            .ytChipShapeInactive {
                color: rgb(var(--main-text));
                background: rgba(var(--button-color), 0.1);
            }

            yt-icon-button.yt-live-chat-item-list-renderer,
            yt-icon-button.yt-live-chat-item-list-renderer[disabled],
            yt-icon.yt-live-chat-ticker-renderer {
                background: rgb(var(--main-color));
                color: rgb(var(--black, var(--white, var(--main-background))));
                --yt-spec-touch-response: currentcolor;
            }

            .yt-tab-shape-wiz__tab {
                color: var(--secondary-text);
            }

            .yt-tab-shape-wiz:hover .yt-tab-shape-wiz__tab-bar {
                background: var(--secondary-text)
            }

            .yt-tab-shape-wiz__tab--tab-selected,
            .yt-tab-shape-wiz:focus .yt-tab-shape-wiz__tab {
                color: rgb(var(--main-color));
            }

            .yt-tab-group-shape-wiz__slider {
                background: rgb(var(--main-color));
            }

            tp-yt-paper-item[disabled],
            .tp-yt-paper-item.tp-yt-paper-item[disabled] {
