            }

            yt-notification-action-renderer[ui-refresh] yt-icon-button.yt-notification-action-renderer,
            .yt-spec-button-shape-next--mono-inverse.yt-spec-button-shape-next--text {
                color: currentcolor;
                --yt-spec-touch-response: currentcolor;
            }

            .yt-spec-button-shape-next--mono-inverse.yt-spec-button-shape-next--text:hover {
                background: rgba(var(--white, var(--main-backgrounda)), 0.2);
            }

            #voice-search-button .yt-spec-button-shape-next--overlay.yt-spec-button-shape-next--text:hover {
                background: rgba(var(--button-color), 0.20);
                --yt-spec-static-overlay-touch-response: rgb(var(--main-text));
            }

            yt-live-chat-membership-item-renderer {
                --yt-live-chat-footer-button-text-color: rgb(var(--black, var(--white, var(--main-background))));
                --yt-live-chat-footer-button-text-background-color: rgb(var(--main-color));
            }

            .ytp-flyout-cta .ytp-flyout-cta-action-button.ytp-flyout-cta-action-button-rounded {
                background: rgb(var(--main-color));
                color: rgb(var(--black, var(--white, var(--main-background)))) !important;
            }

            yt-live-chat-toast-renderer .yt-spec-button-shape-next--call-to-action-inverse.yt-spec-button-shape-next--text {
                color: rgb(var(--main-color));
                &:hover {
                    background: rgba(var(--main-color), 0.30);
                }
            }

            .yt-spec-button-shape-next--call-to-action-inverse.yt-spec-button-shape-next--text {
                color: rgb(var(--black, var(--white, var(--main-background))));
                &:hover {
                    background: rgba(var(--black, var(--white, var(--main-background))), 0.3);
                }
            }

            /* Live chat */
            #live-chat-dialog-body .bold.yt-formatted-string:not(a) {
                color: rgb(var(--main-text)) !important;
            }

            yt-live-chat-app ::-webkit-scrollbar-thumb {
                background: var(--secondary-text);
                &:hover {
                    background: rgba(var(--main-text), 0.38);
                }
            }

            /* Hyperlinks */
            .yt-core-attributed-string__link--call-to-action-color:not(.yt-core-attributed-string--link-inherit-color),
            .yt-channel-external-link-view-model-wiz__link,
            :is([style*="color: rgb(62, 166, 255);"], [style*="color: rgb(6, 95, 212);"]) {
                color: rgb(var(--main-color)) !important;
            }

            .yt-core-attributed-string--highlight-text-decorator {
                background: var(--button-color) !important;
            }

            /* Badges/icons */
            .badge-style-type-live-now-alternate.ytd-badge-supported-renderer yt-icon,
            .badge-style-type-live-now-alternate.ytd-badge-supported-renderer,
            ytd-thumbnail-overlay-time-status-renderer[overlay-style=LIVE] #time-status.ytd-thumbnail-overlay-time-status-renderer,
            .yt-spec-avatar-shape__badge-text {
                color: rgba(var(--white, var(--white-2, var(--black, var(--main-background))))) !important;
            }

            .yt-spec-icon-badge-shape--type-notification .yt-spec-icon-badge-shape__badge,
            .ytp-videowall-still-info-live,
            .ytp-live-badge[disabled]:before,
            .badge-shape-wiz--thumbnail-live,
            .yt-spec-avatar-shape__live-badge {
                background: var(--red) !important;
                color: rgba(var(--white, var(--white-2, var(--black, var(--main-background))))) !important;
                border: none !important;
            }

            .yt-spec-avatar-shape--live-ring {
                border-color: var(--red);
            }

            .yt-spec-avatar-shape--cairo-refresh.yt-spec-avatar-shape--live-ring::after {
                background: var(--red);
            }

            .guide-entry-badge.ytd-guide-entry-renderer {
