            }

            .yt-spec-icon-badge-shape,
            .yt-spec-profile-page-header-information-view-model-shape__profile-page-header-title,
            .yt-spec-icon-badge-shape--style-overlay .yt-spec-icon-badge-shape__icon {
                color: rgb(var(--main-text));
            }

            ytd-author-comment-badge-renderer[creator] {
                --ytd-author-comment-badge-background-color: rgb(var(--main-color)) !important;
                --ytd-author-comment-badge-name-color: rgb(var(--black, var(--white, var(--main-background)))) !important;
                --ytd-author-comment-badge-icon-color: rgb(var(--black, var(--white, var(--main-background)))) !important;
            }

            .ytp-settings-button.ytp-hd-quality-badge:after,
            .ytp-settings-button.ytp-hdr-quality-badge:after,
            .ytp-settings-button.ytp-4k-quality-badge:after,
            .ytp-settings-button.ytp-5k-quality-badge:after,
            .ytp-settings-button.ytp-8k-quality-badge:after,
