        }

        .guide-icon.ytd-guide-entry-renderer,
        yt-img-shadow.ytd-guide-entry-renderer {
            margin: 0px;
        }

        yt-img-shadow.ytd-guide-entry-renderer {
            margin-bottom: 3px;
        }

        tp-yt-paper-item.ytd-guide-entry-renderer {
            flex-direction: column;
            justify-content: center;
        }

        ytd-guide-entry-renderer,
        #endpoint.yt-simple-endpoint.ytd-guide-entry-renderer:is(:hover, :focus, :active),
        yt-interaction.ytd-guide-entry-renderer,
        tp-yt-paper-item.ytd-guide-entry-renderer {
            border-radius: 50%;
            --paper-item-focused-before-border-radius: 50%;
        }
    }

    if compact-header-bar {
        html {
            --ytd-toolbar-height: 36px;
        }

        ytd-feed-filter-chip-bar-renderer {
            --ytd-rich-grid-chips-bar-top: var(--ytd-toolbar-height);
        }

        ytd-app:not([scrolling]) {
            --ytd-masthead-height: var(--ytd-toolbar-height) !important;
        }

        .ytd-masthead:is(#container, #background),
        #header.ytd-app,
        ytd-masthead.shell {
            height: var(--ytd-toolbar-height);
        }

        #player.skeleton.theater {
            margin-top: var(--ytd-toolbar-height);
        }

        yt-searchbox,
        .ytd-searchbox:is(#search-form, #search-icon-legacy) {
            height: 30px !important;
        }

        yt-icon-button:is(.ytd-topbar-menu-button-renderer, .ytd-masthead, .ytd-notification-topbar-button-renderer),
        #masthead .yt-spec-button-shape-next--icon-only-default,
        #guide-button.ytd-app,
        .ytSearchboxComponentClearButton {
            width: 30px !important;
            height: 30px !important;
            padding: 3px;
        }

        .ytSearchboxComponentSuggestionsContainer {
          top: 30px;
        }

        yt-icon.ytd-logo {
            padding-top: 8px;
            padding-bottom: 8px;
        }

        #container.ytd-masthead,
        #header.ytd-app {
            padding: 0 21px;
        }
    }

    if (no-recomendations-bar) {
        [page-subtype="home"] ytd-feed-filter-chip-bar-renderer,
        #home-chips,
        yt-related-chip-cloud-renderer {
            display: none if no-recomendations-bar;
        }

        ytd-masthead[frosted-glass=with-chipbar] #background.ytd-masthead {
            height: var(--ytd-toolbar-height);
        }
    }

    ytd-rich-section-renderer:has([is-post]) {
        display: none if no-yt-posts;
    }

    #expander {
        --ytd-expander-max-lines: none !important if expanded-comments;
    }

    if sidebar-comments {
        ytd-watch-flexy[is-two-columns_] {
            #comments {
                position: absolute;
                width: var(--ytd-watch-flexy-sidebar-width);
                height: calc(100vh - var(--ytd-margin-6x) - var(--ytd-toolbar-height));
                overflow: auto;
                &::-webkit-scrollbar {
                    width: 16px;
                }
                &::-webkit-scrollbar-thumb {
                    height: 56px;
                    border-radius: 8px;
                    border: 4px solid transparent;
                    background-clip: content-box;
                    background-color: var(--yt-spec-text-secondary);
                    &:hover {
                        background-color: var(--yt-spec-icon-disabled);
                    }
                }
            }

            &[default-layout] #comments {
                top: calc(0px - var(--ytd-watch-flexy-panel-max-height) - var(--ytd-margin-3x));
            }

            &:not([default-layout]) #comments {
                top: var(--ytd-margin-3x);
            }

            body[dir="ltr"] & #comments {
                right: calc(0px - var(--ytd-watch-flexy-sidebar-width) - var(--ytd-margin-6x));
            }

            body[dir="rtl"] & #comments {
                left: calc(0px - var(--ytd-watch-flexy-sidebar-width) - var(--ytd-margin-6x));
            }

            &:has(#comments:not([hidden])) #secondary.ytd-watch-flexy {
                top: calc(100vh - var(--ytd-margin-6x) - var(--ytd-toolbar-height) + var(--ytd-margin-6x));
            }

            &:has(ytd-live-chat-frame:not([collapsed])),
            &:has(ytd-engagement-panel-section-list-renderer[visibility="ENGAGEMENT_PANEL_VISIBILITY_EXPANDED"]) {
                #comments {
                    display: none;
                }

                #secondary.ytd-watch-flexy {
                    top: 0 !important;
                }
            }
        }
    }

    if pin-video-on-scroll {
        :root {
            --pv-height: .5625 * pinned-video-width;

            if pinned-video-position=="Bottom-right" {
                --pv-right: 10px;
                --pv-bottom: 10px;
            }

            else if pinned-video-position=="Bottom-center" {
                 --pv-right: 50vw;
                 --pv-bottom: 10px;
                 --pv-transform: translateX(50%);
            }

            else if pinned-video-position=="Bottom-left" {
                --pv-left: 10px;
                --pv-bottom: 10px;
            }

            else if pinned-video-position=="Top-right" {
                --pv-right: 10px;
            }

            else if pinned-video-position=="Top-center" {
                --pv-right: 50vw;
                --pv-transform: translateX(50%);
            }

            else if pinned-video-position=="Top-left" {
                --pv-left: 10px;
            }

            else if pinned-video-position=="Center-right" {
                --pv-right: 10px;
                --pv-bottom: 50vh;
                --pv-transform: translateY(50%);
            }

            else if pinned-video-position=="Center-left" {
                --pv-left: 57.5px;
                --pv-bottom: 50vh;
                --pv-transform: translateY(50%);
            }
        }

        var = ""
        var = ".html5-video-player:not(.paused-mode)" if ignore-paused-videos

        #player-container[style="touch-action: unset;"] {
            .html5-video-player {
                z-index: 2050;
            }

            .ended-mode > .html5-video-container {
                display: none;
            }

            .html5-video-container {
                background: rgba(0, 0, 0, 0.5);
                backdrop-filter: blur(5px);
                border-radius: 12px;
            }

            {var} {
                .video-stream {
                    position: initial;
                    height: var(--pv-height) !important;
                    width: pinned-video-width !important;
                    border-radius: 12px;
                }

                .html5-video-container,
                .ytp-caption-window-container,
                .ytp-ad-module {
                    position: fixed;
                    height: var(--pv-height) !important;
                    width: pinned-video-width !important;
                    bottom: var(--pv-bottom, calc(100vh - var(--pv-height) - 10px));
                    transform: var(--pv-transform, initial);
                    top: initial !important;
                    right: var(--pv-right, initial);
                    left: var(--pv-left, initial);
                    z-index: 999;
                }

                .ytp-caption-segment {
                    font-size: pinned-video-subs-size !important;
                }

                .caption-window {
                    width: calc(100% - 20px) !important;
                    left: 10px !important;
                    top: initial !important;
                    bottom: 10px !important;
                    margin-left: 0px !important;
                    margin-bottom: 0px;
                    text-align: center !important;
                    cursor: default;
                }
            }
        }
    }

    if theater {
        ytd-watch-flexy[theater]:not([fullscreen]) {
            #full-bleed-container.ytd-watch-flexy {
                height: 100vh;
                max-height: none;
                margin-top: calc(0px - var(--ytd-toolbar-height));
            }
        }

        ytd-watch-flexy[fixed-panels] #chat.ytd-watch-flexy {
            top: 0;
        }

        #masthead[is-watch-page][theater]:not([fullscreen]) {
            position: absolute;
            transform: translateY(calc(0px - var(--ytd-toolbar-height)));
            padding-bottom: 20px;
            transition: transform .3s ease, padding .3s ease;
            &:hover {
                transform: initial;
                padding-bottom: 0;
            }
        }

        #player.skeleton.theater {
            height: 100vh;
            margin-top: 0;
            max-height: none;
        }
    }

    if ambient-mode {
        ytd-watch-flexy:not([default-layout]) #player.ytd-watch-flexy {
            display: block !important;
            position: absolute;
            top: 30px;
            left: 0;
            right: 0;
        }

        ytd-watch-flexy:not([default-layout]) #cinematics.ytd-watch-flexy > div > div {
            transform: scale(1, 2) !important; 
            width: 100vw;
        }

        ytd-watch-flexy .html5-video-player {
            z-index: 2000;
        }

        body {
            overflow-x: hidden;
        }

        body:has(#player-container[style="touch-action: auto;"]) {
            --headerBgOpacity: 0;
        }

        ytd-masthead[is-watch-page] #background.ytd-masthead {
            transition: opacity .25s;
            opacity: var(--headerBgOpacity, 1);
        }
    }

    .ytp-autohide .ytp-ce-element.ytp-ce-element-show {
