    }

    if subscribe-color {
        ytd-subscribe-button-renderer .yt-spec-button-shape-next--filled,
        [button-style="COMPACT_GRAY"]:not([subscribed]) .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--tonal,
        .yt-subscribe-button-view-model-wiz__container > .yt-spec-button-shape-next--mono.yt-spec-button-shape-next--filled,
