/* ==UserStyle==
@name         Codeforces Dark
@namespace    USO Archive
@author       abhishekchakraborty
@description  Dark theme for Coding competition site, <a href="https://codeforces.com">Codeforces</a>
@version      20191017.03.40
@license      CC-BY-4.0
@preprocessor uso
==/UserStyle== */
@-moz-document domain("codeforces.com") {
body {
  background-color: #222;
  color: #ccc;
}

a:link {
  color: #428BCA;  /* from bibsonomy */
}

a:visited {
  color: #068eed; /* from bibsonomy #057fd4; */
}

/** Site Logo at top **/
img[src*="codeforces-logo-with-telegram.png"] {
  filter: invert(86.5%);
}

/* 'Enter', 'Register' */
.lang-chooser {
  color: #666;
}

/* Bell Icon (on Login) */
.header-bell__img {
  filter: invert(86.5%);
}

/* Overlay on click of Bell Icon */
.bell-details {
  background-color: #282828;
  border-color: #333;
}

.bell-details__no-new {
  color: #aaa !important;
}

.bell-details__recent-link {
  border-top-color: #444;
}


/*** Top Header ***/

.roundbox {
  background-color: #282828;
  border-color: #333;
}

.roundbox .roundbox-lt, .roundbox .roundbox-rt, .roundbox .roundbox-lb, .roundbox .roundbox-rb {
  filter: invert(80%);
}

.menu-list li a {
  color: #aaa;
}

/* Search box */
.search {
  background-color: #222;
  border-color: #333;
  color: #ccc;
}

/******** Home Page - codeforces.com ********/

/*** Message ***/
div.alert-success {
  background-color: #336534;
  border-color: #3c763d;
  text-shadow: none;
  color: #ccc;
}

div.alert-success a:link {
  color: #6aa3d5;
}

/*** 'Pay attention' box on left ***/

.roundbox .caption {
  color: #aaa;
}

.roundbox .titled {
  border-bottom-color: #333;
}

div[style*="border-bottom: 1px solid rgb(185, 185, 185);"] {
  border-bottom-color: #333 !important;
}

/*** '<username>' box on left (on login) ***/

img[src="//userpic.codeforces.com/no-avatar.jpg"] {
  opacity: 0.3;
}

/*** 'Top rated' box on left ***/

.roundbox table.rtable {
  color: #aaa;
}

/* Legendary Username */
.legendary-user-first-letter {
  color: #080 !important; /* from cppreference */
}

.user-legendary {
  color: #999 !important; /* from cppreference */
}

.roundbox .rtable td, .roundbox .rtable th {
  border-color: #333;
}

.roundbox .dark {
  background-color: #222;
}

.roundbox .bottom-links {
  background-color: #333;
  border-top-color: #333;
}

/*** Body ***/

div.ttypography {
  color: #ccc;
}

.topic .content {
  border-left-color: #333;
}

div.ttypography a:hover, div.ttypography a:focus {
  background-color: #333;
  color: inherit;
}

/* Image */
div.ttypography img, div.ttypography figure {
  filter: invert(86.5%);
}

/* Table */

div.ttypography table.bordertable {
  border-color: #666;
}

div.ttypography table {
  color: #ccc;
}

div.ttypography tbody th {
  border-top-color: #666;
}

div.ttypography .bordertable tbody th {
  border-right-color: #666;
}

div.ttypography tbody td {
  border-top-color: #666;
}

div.ttypography .bordertable tbody td {
  border-right-color: #666;
}

div.ttypography tbody tr:hover th, div.ttypography tbody tr:hover td {
  background-color: #333;
}

/* User Link categories */

.user-admin {
  color: #F64A8A !important; /* from latexcolor.com */
}

.user-red {
  color: #DC143C !important; /* from latexcolor.com */
