      --yellow-400: fade(@yellow, 70%);
      --yellow-500: fade(@yellow, 90%);

      --red-200: fade(@red, 40%);
      --red-400: fade(@red, 70%);
      --red-500: fade(@red, 80%);

      --scrollbar: @surface1;
