    .ace-tm .ace_entity.ace_name.ace_function {
        color: var(--blue) !important;
    }

    .ace-tm .ace_keyword.ace_operator,
    #runtime-placeholder,
    #memory-placeholder {
        color: var(--text) !important;
    }

    .ace-tm .ace_marker-layer .ace_selection {
        background: var(--highlight-3);
    }

    /* Notifications Page */
    .notification-app-container .notification-list-title {
        margin-top: 1em;
    }

    .notifications-container .notification-list-item.notification-list-unread-item {
        background: var(--bg);
        border-bottom: 1px var(--bg-2) solid;
    }

    .notifications-container .notification-list-item.notification-list-unread-item,
    .notification-content-title {
        color: var(--text) !important;
    }

    .btn.notification-content-action-btn {
        background: var(--blue-dark) !important;
        color: #fff !important;
    }

}
